import { Vue, Component, Prop, Ref } from 'vue-property-decorator';

@Component({
  name: 'BaseImage',
})
export default class extends Vue {
  @Prop({ type: String, required: true })
  private src!: string;

  @Prop({ type: String, default: '' })
  private title!: string;

  @Prop({ type: String, default: '' })
  private alt!: string;

  private isError = false;

  @Ref('img')
  private img!: HTMLElement;

  private onError(): void {
    this.isError = true;
  }

  private mounted(): void {
    if (this.img) {
      this.img.addEventListener('error', this.onError);
    }
  }

  private beforeDestroy(): void {
    if (this.img) {
      this.img.removeEventListener('error', this.onError);
    }
  }
}

import { Vue, Component } from 'vue-property-decorator';

@Component({
  name: 'Card',
})
export default class extends Vue {}

import { Vue, Component, Prop, InjectReactive } from 'vue-property-decorator';
import { Catalogue } from '@/types/article';

@Component({
  name: 'CatalogueItem',
})
export default class extends Vue {
  @Prop({ type: Array, default: () => ([]) })
  private catalogue!: Array<Catalogue>;

  @Prop({ type: Number, default: 0 })
  private level!: number;

  @InjectReactive('tag')
  private tag!: string;

  @InjectReactive('activedId')
  private activedId!: string;

  @InjectReactive('showPoint')
  private showPoint!: string;

  @InjectReactive('preventParentClick')
  private preventParentClick!: boolean;

  @InjectReactive('onEmitChange')
  private onEmitChange!: (id: string) => string;

  private get style(): { [key: string]: string } {
    const baseLeft = this.showPoint ? 15 : 0;

    return {
      paddingLeft: `${baseLeft + this.level * 20}px`,
    };
  }

  private isParent(catalogue: Catalogue): boolean {
    return !!catalogue?.children?.length;
  }

  private onChange(catalogue: Catalogue): void {
    if (this.preventParentClick && this.isParent(catalogue)) {
      return;
    }

    this.onEmitChange(catalogue.id);
  }
}

import { Vue, Component, Prop, Emit, ProvideReactive } from 'vue-property-decorator';
import { Catalogue } from '@/types/article';
import CatalogueItem from './CatalogueItem.vue';
import Card from '@commonComponents/Card.vue';
import Icon from '@commonComponents/Icon.vue';
import { validator } from '@utils/index';

@Component({
  name: 'Catalogue',
  components: {
    Card,
    CatalogueItem,
    Icon,
  },
})
export default class extends Vue {
  @Prop({ type: Object }) private catalogue!: Catalogue;
  @Prop({ type: Boolean, default: true }) private toggle!: boolean;

  @Prop({ type: String, default: 'a', validator: validator(['a', 'div']) })
  @ProvideReactive('tag')
  private tag!: string;

  @Prop({ type: String })
  @ProvideReactive('activedId')
  private activedId!: string;

  @Prop({ type: Boolean, default: false })
  @ProvideReactive('preventParentClick')
  private preventParentClick!: boolean;

  @Prop({ type: Boolean, default: true })
  @ProvideReactive('showPoint')
  private showPoint!: boolean;

  private get _catalogue(): Array<Catalogue> {
    return this.catalogue.children;
  }

  @Emit('on-toggle')
  private onEmitToggle(toggle: boolean): boolean {
    return toggle;
  }

  @Emit('on-change')
  @ProvideReactive('onEmitChange')
  private onEmitChange(id: string): string {
    return id;
  }
}

import { Vue, Component, Prop, Emit } from 'vue-property-decorator';
import BaseImage from '@commonComponents/BaseImage.vue';
import Icon from '@commonComponents/Icon.vue';

@Component({
  name: 'ImagePreview',
  components: {
    Icon,
    BaseImage,
  },
})
export default class extends Vue {
  @Prop({ type: String, default: '' })
  private src!: string;

  @Prop({ type: String, default: '' })
  private alt!: string;

  @Prop({ type: String, default: '' })
  private title!: string;

  private overflowCache: string = '';

  @Emit('on-close')
  private onEmitClose(): boolean {
    return true;
  }

  private mounted(): void {
    this.overflowCache = document.body.style.overflow;
    document.body.style.overflow = 'hidden';
  }

  private beforeDestroy(): void {
    document.body.style.overflow = this.overflowCache;
  }
}

import { Vue, Component, Prop, Watch, Emit } from 'vue-property-decorator';
import marked from 'marked';
import prismjs from 'prismjs';
import sanitize from 'sanitize-html';
import { Catalogue as TCatalogue } from '@/types/article';
import YImage from '@commonComponents/YImage.vue';
import YCode from '@commonComponents/YCode.vue';
import { renderer, Catalogue } from '@utils/markdown';

// theme和code的相关css
import 'prismjs/themes/prism.css';
import 'github-markdown-css';

const components = {
  'y-image': YImage,
  'y-code': YCode,
};

@Component({
  name: 'Markdown',
})
export default class extends Vue {
  @Prop({ type: String, default: '' }) private content!: string;

  private html = '';
  private codeMap: { [key: string]: string } = {};

  private get markdownIs(): unknown {
    const codeMap = this.codeMap;

    return {
      template: `<div class="markdown-body">${this.html}</div>`,
      components,
      data() {
        return {
          codeMap,
        };
      },
    };
  }

  @Watch('content')
  private onWatchContentChange(content: string): void {
    this.getHTML(content);
  }

  @Emit('on-catalogue')
  private onEmitCatalogue(catalogue: TCatalogue): TCatalogue {
    return catalogue;
  }

  private getHTML(content: string): void {
    const catalogue = new Catalogue();
    let headingIndex = 0;
    let codeIndex = 0;

    marked.use({
      renderer: {
        heading: (text, level, raw): string => {
          const id = `heading-${headingIndex}`;
          headingIndex += 1;
          catalogue.resolveCatalogue(level, raw, id, catalogue.catalogueCache);
          return `<h${level} id="${id}">${text}</h${level}>`;
        },
        code: (code: string, language: string): string => {
          const realLanguage = prismjs.languages[language] ? language : 'text';
          const codeHtml = prismjs.highlight(code, prismjs.languages[realLanguage], realLanguage);
          codeIndex += 1;
          this.codeMap[codeIndex] = codeHtml;
          return `<y-code :code="codeMap[${codeIndex}]" language="${realLanguage}"></y-code>`;
        },
        ...renderer,
      },
    });
    const dirtyContent = marked(content);

    this.html = sanitize(dirtyContent, {
      allowedTags: sanitize.defaults.allowedTags.concat(Object.keys(components)),
      allowedAttributes: {
        ...sanitize.defaults.allowedAttributes,
        '*': [':code', 'language', 'src', 'alt', 'title', 'class', 'id'],
      },
    });

    this.onEmitCatalogue(catalogue.getCatalogue());
  }

  private created(): void {
    this.getHTML(this.content);
  }
}

