import { Component, InjectReactive, Watch } from 'vue-property-decorator';
import { Article, Articles } from '@/types/article';
import { Catalogue as TCatalogue } from '@/types/article';
import Page from '@mixins/Page';
import ArticleInfo from '@components/ArticleInfo.vue';
import Card from '@commonComponents/Card.vue';
import Catalogue from '@commonComponents/Catalogue/index.vue';
import Markdown from '@commonComponents/Markdown.vue';
import YButton from '@commonComponents/YButton.vue';
import YLine from '@commonComponents/Line.vue';
import BaseImage from '@commonComponents/BaseImage.vue';
import RouteName from '@router/routerName';
import { getLocalStorage, setLocalStorage } from '@utils/index';
import { isServerRender } from '@utils/is';
import { StorageKey } from '@constant/index';

@Component({
  name: 'Article',
  components: {
    ArticleInfo,
    Card,
    Catalogue,
    Markdown,
    YButton,
    YLine,
    BaseImage,
  },
})
export default class extends Page {
  @InjectReactive('articles')
  private articles!: Articles;

  private createTime = '';
  private category = '';
  private title = '';
  private content = '';
  private banner = '';
  private lastArticle!: Article | null;
  private nextArticle!: Article | null;
  private toggle: boolean = true;
  private catalogue: TCatalogue = {
    title: '',
    id: '',
    level: 0,
    children: [],
  };
  private RouteName = RouteName;

  @Watch('$route.params.id')
  private onWatchId(id: string): void {
    this.getArticle(id);
    this.setTitle();
  }

  private getArticle(id: string) {
    this.lastArticle = null;
    this.nextArticle = null;

    let currentArticle;
    let isFind = false;
    for (const article of this.articles) {
      if (isFind) {
        this.nextArticle = article;
        break;
      }

      if (article.id === id) {
        currentArticle = article;
        isFind = true;
      }

      if (!isFind) {
        this.lastArticle = article;
      }
    }

    if (currentArticle) {
      this.createTime = currentArticle.createTime;
      this.category = currentArticle.category;
      this.title = currentArticle.title;
      this.content = currentArticle.content;
      this.banner = currentArticle.banner;
    }
  }

  private getCatalogue(catalogue: TCatalogue): void {
    catalogue.title = this.title;
    this.catalogue = catalogue;
  }

  private onToggle(toggle: boolean): void {
    setLocalStorage(StorageKey.ArticleCatalogue, toggle);
    this.toggle = toggle;
  }

  private created(): void {
    const id = this.$route.params.id;
    this.getArticle(id);
    this.toggle = isServerRender ? false : getLocalStorage(StorageKey.ArticleCatalogue) !== 'false';
  }
}

