import Comp from './index.vue';

export default class ImagePreview {
  private el = document.body;
  private src: string = '';
  private title: string = '';
  private alt: string = '';
  private component!: Vue;

  constructor(src: string, title: string, alt: string) {
    this.src = src;
    this.title = title;
    this.alt = alt;

    const instance = new Vue({
      render: (h) => h(Comp, {
        props: {
          src: this.src,
          title: this.title,
          alt: this.alt,
        },
        on: {
          'on-close': this.destroy,
        },
      }),
    });
    this.component = instance.$mount();
  }

  public mounted = (): void => {
    this.el.appendChild(this.component.$el);
  }

  public destroy = (): void => {
    this.component.$destroy();
    this.el.removeChild(this.component.$el);
  }
}

