import { Catalogue as TCatalogue } from '@/types/article';

export const renderer = {
  image(href: string | null, title: string | null, text: string): any {
    return `<y-image src="${href}" title="${title || ''}" alt="${text || ''}"></y-image>`;
  },
};

export class Catalogue {
  public catalogue!: TCatalogue;
  public catalogueCache!: TCatalogue;

  constructor() {
    this.catalogue = {
      title: '',
      id: '',
      level: 0,
      children: [],
    };
    this.catalogueCache = this.catalogue;
  }

  public resolveCatalogue(level: number, title: string, id: string, lastCatalogue: TCatalogue): void {
    if (level > lastCatalogue.level) {
      const item = {
        title,
        id,
        level,
        children: [],
        parent: lastCatalogue,
      };
      lastCatalogue.children.push(item);
      this.catalogueCache = item;
    } else if (lastCatalogue.parent) {
      this.catalogueCache = lastCatalogue.parent;
      this.resolveCatalogue(level, title, id, lastCatalogue.parent);
    }
  }

  public deleteCatalogueKey(catalogue: TCatalogue): void {
    if (catalogue.parent) {
      delete catalogue.parent;
    }

    catalogue.children.forEach((item) => {
      this.deleteCatalogueKey(item);
    });
  }

  public getCatalogue(): TCatalogue {
    this.deleteCatalogueKey(this.catalogue);
    return this.catalogue;
  }
}

