@Component({
  name: 'CatalogueItem',
})
export default class extends Vue {
  @Prop({ type: Array, default: () => ([]) })
  private catalogue!: Array<Catalogue>;

  @Prop({ type: Number, default: 0 })
  private level!: number;

  @InjectReactive('tag')
  private tag!: string;

  @InjectReactive('activedId')
  private activedId!: string;

  @InjectReactive('showPoint')
  private showPoint!: string;

  @InjectReactive('preventParentClick')
  private preventParentClick!: boolean;

  @InjectReactive('onEmitChange')
  private onEmitChange!: (id: string) => string;

  private get style(): { [key: string]: string } {
    const baseLeft = this.showPoint ? 15 : 0;

    return {
      paddingLeft: `${baseLeft + this.level * 20}px`,
    };
  }

  private isParent(catalogue: Catalogue): boolean {
    return !!catalogue?.children?.length;
  }

  private onChange(catalogue: Catalogue): void {
    if (this.preventParentClick && this.isParent(catalogue)) {
      return;
    }

    this.onEmitChange(catalogue.id);
  }
}

