    const codeMap = this.codeMap;

    return {
      template: `<div class="markdown-body">${this.html}</div>`,
      components,
      data() {
        return {
          codeMap,
        };
      },
    };
  }

  @Watch('content')
  private onWatchContentChange(content: string): void {
    this.getHTML(content);
  }

  @Emit('on-catalogue')
  private onEmitCatalogue(catalogue: TCatalogue): TCatalogue {
    return catalogue;
  }

  private getHTML(content: string): void {
    const catalogue = new Catalogue();
    let headingIndex = 0;
    let codeIndex = 0;

    marked.use({
      renderer: {
        heading: (text, level, raw): string => {
          const id = `heading-${headingIndex}`;
          headingIndex += 1;
          catalogue.resolveCatalogue(level, raw, id, catalogue.catalogueCache);
          return `<h${level} id="${id}">${text}</h${level}>`;
        },
        code: (code: string, language: string): string => {
          const realLanguage = prismjs.languages[language] ? language : 'text';
          const codeHtml = prismjs.highlight(code, prismjs.languages[realLanguage], realLanguage);
          codeIndex += 1;
          this.codeMap[codeIndex] = codeHtml;
          return `<y-code :code="codeMap[${codeIndex}]" language="${realLanguage}"></y-code>`;
        },
        ...renderer,
      },
    });
    const dirtyContent = marked(content);

    this.html = sanitize(dirtyContent, {
      allowedTags: sanitize.defaults.allowedTags.concat(Object.keys(components)),
      allowedAttributes: {
        ...sanitize.defaults.allowedAttributes,
        '*': [':code', 'language', 'src', 'alt', 'title', 'class', 'id'],
      },
    });

    this.onEmitCatalogue(catalogue.getCatalogue());
  }

  private created(): void {
    this.getHTML(this.content);
  }
}

