import { Component, InjectReactive } from 'vue-property-decorator';
import { Columns, Column } from '@/types/columns';
import { Catalogue as TCatalogue } from '@/types/article';
import Catalogue from '@commonComponents/Catalogue/index.vue';
import Markdown from '@commonComponents/Markdown.vue';
import Card from '@commonComponents/Card.vue';
import Page from '@mixins/Page';

interface Article {
  title: string;
  content: string;
}

type Articles = Array<Article>;

function getCatalogue(columns: Columns, level: number = 0): {
  articles: Articles,
  catalogue: Array<TCatalogue>,
} {
  const catalogue: Array<TCatalogue> = [];
  const articles: Articles = [];
  const newLevel = level + 1;

  columns.forEach((column) => {
    const catalogueItem: TCatalogue = {
      level: newLevel,
      title: column.title,
      id: column.title,
      children: [],
    };

    if (column.content) {
      articles.push({
        title: column.title,
        content: column.content,
      });
    }

    if (column.articles) {
      const { catalogue: subCatalogue, articles: subArticles } = getCatalogue(column.articles, newLevel);
      catalogueItem.children = subCatalogue;
      articles.push(...subArticles);
    }

    catalogue.push(catalogueItem);
  });

  return {
    catalogue,
    articles,
  };
}

function findArticleByTitle(title: string, articles: Articles): { current: Column, next?: Column } | false {
  const len = articles.length;
  let isExist = false;
  let currentIndex: number = 0;

  for (let i = 0; i < len; i++) {
    const article = articles[i];

    if (article.title === title) {
      isExist = true;
      currentIndex = i;
      break;
    }
  }

  if (isExist) {
    if (articles[currentIndex + 1]) {
      return {
        current: articles[currentIndex],
        next: articles[currentIndex + 1],
      };
    }

    return {
      current: articles[currentIndex],
    };
  }

  return false;
}

@Component({
  name: 'Column',
  components: {
    Card,
    Catalogue,
    Markdown,
  },
})
export default class extends Page {
  private title = '';
  private banner = '';
  private articles: Articles = [];
  private catalogue: TCatalogue = {
    title: '',
    id: '',
    level: 0,
    children: [],
  };

  @InjectReactive('columns')
  private columns!: Columns;

  private get article(): { current: Column, next?: Column } {
    const result = findArticleByTitle(this.currentArticleId, this.articles);

    if (result) {
      return result;
    }

    return {
      current: {
        title: '',
        content: '',
      },
    };
  }

  private get currentArticleId(): string {
    return this.$route.query?.title as string || this.articles[0]?.title || '';
  }

  private getColumn(id: string): void {
    let articles: Columns = [];

    for (const column of this.columns) {
      if (column.id === id) {
        this.title = column.title;
        this.banner = column.banner || '';
        articles = column.articles || [];
        break;
      }
    }

    const { catalogue: catalogueRes, articles: articlesRes } = getCatalogue(articles);
    const catalogue = {
      title: this.title,
      id: '',
      level: 0,
      children: catalogueRes,
    };

    this.articles = articlesRes;
    this.catalogue = catalogue;
  }

  private onChange(id: string): void {
    if (this.currentArticleId === id) {
      return;
    }

    this.$router.push({
      query: {
        title: id,
      },
    });
  }

  private created(): void {
    this.getColumn(this.$route.params.columnId);
  }
}

