import Card from '@commonComponents/Card.vue';
import BaseImage from '@commonComponents/BaseImage.vue';
import RouteName from '@router/routerName';

function getCount(articles: Columns = []): number {
  let count = 0;

  for (const article of articles) {
    if (article.articles) {
      count += getCount(article.articles);
    } else if (article.content) {
      count += 1;
    }
  }

  return count;
}

@Component({
  name: 'ArticleCard',
  components: {
    Card,
    BaseImage,
    ColumnInfo,
  },
})
