import { Vue, Component, ProvideReactive } from 'vue-property-decorator';
import articles from './y_system_articles';
import columns from './y_system_columns';
import { MenuItem, Website } from '@/types/app';
import { UserInfo as TypeUserInfo } from '@/types/userInfo';
import { Article, Articles, Categories } from '@/types/article';
import { Columns } from '@/types/columns';
import RouteName from '@router/routerName';
import BackTop from '@commonComponents/BackTop.vue';
import AppNav from '@components/AppNav.vue';
import AppFooter from '@components/AppFooter.vue';
import { parse } from '@utils/index';

@Component({
  name: 'App',
  components: {
    BackTop,
    AppNav,
    AppFooter,
  },
})
export default class extends Vue {
  @ProvideReactive('userInfo')
  private userInfo: TypeUserInfo = {
    name: '',
    description: '',
    avatar: '',
    links: [],
  };

  @ProvideReactive('articles')
  private articles: Articles = [];

  @ProvideReactive('categories')
  private categories: Categories = [];

  @ProvideReactive('columns')
  private columns: Columns = [];

  @ProvideReactive('footers')
  private footers: Array<string> = [];

  @ProvideReactive('website')
  private website: Website = {
    title: '',
    description: '',
  };

  private get menu(): Array<MenuItem> {
    const menu: Array<MenuItem> = [
      { title: '首页', route: RouteName.HOME },
    ];

    if (this.columns?.length) {
      menu.push({ title: '专栏', route: RouteName.COLUMNS });
    }

    return menu;
  }

  private init(): void {
    const {
      VUE_APP_INFO_USERINFO,
      VUE_APP_INFO_WEBSITE,
      VUE_APP_INFO_FOOTERS,
    } = process.env;

    // 网站信息
    this.website = parse(this.website, VUE_APP_INFO_WEBSITE);
    // 用户资料
    this.userInfo = parse(this.userInfo, VUE_APP_INFO_USERINFO);
    // 文章
    this.articles = (articles as Articles).sort((before: Article, after: Article) => {
      const beforeDate = new Date(before.createTime);
      const afterDate = new Date(after.createTime);

      return afterDate.getTime() - beforeDate.getTime();
    });
    // 分类
    this.categories = Array.from(new Set(['全部'].concat(this.articles.map((article) => article.category))));
    // 专栏
    this.columns = columns as Columns;
    // 底部信息
    this.footers = parse(this.footers, VUE_APP_INFO_FOOTERS);
  }

  private created(): void {
    this.init();
  }
}







import { Vue, Component, Prop } from 'vue-property-decorator';
import Icon from '@commonComponents/Icon.vue';
import { scrollTop } from '@utils/index';

@Component({
  name: 'BackTop',
  components: {
    Icon,
  },
})
export default class extends Vue {
  @Prop({ type: Number, default: 30 }) private bottom!: number;
  @Prop({ type: Number, default: 30 }) private right!: number;
  @Prop({ type: Number, default: 1200 }) private duration!: number;

  private get style(): { bottom: string, right: string } {
    return {
      bottom: `${this.bottom}px`,
      right: `${this.right}px`,
    };
  }

  private backTop(): void {
    const top = document.documentElement.scrollTop || document.body.scrollTop;
    scrollTop(top, 0, this.duration);
  }
}







