  const { VUE_APP_INFO_SCRIPTS = '[]' } = process.env;
  const scriptNames = parse([], VUE_APP_INFO_SCRIPTS);

  scriptNames.forEach((scriptInfo: Script) => {
    const path = typeof scriptInfo === 'string' ? `/scripts/${scriptInfo}` : `/scripts/${scriptInfo.src}`;
    const script: HTMLScriptElement = document.createElement('script');
    script.src = path;

    if (typeof scriptInfo !== 'string' && scriptInfo.asyncType) {
      script[scriptInfo.asyncType] = true;
    }

    document.body.appendChild(script);
  });
}

