      component: () => import('@pages/Columns/index.vue'),
      meta: {
        title: '专栏',
      },
    },
    {
      name: RouteName.COLUMN,
      path: '/column/:columnId',
      component: () => import('@pages/Column/index.vue'),
      meta: {
        title(context: Vue) {
          return (context as any).title;
        },
      },
    },
    {
      name: RouteName.NOTFOUND,
      path: '*',
      component: () => import('@pages/NotFound/index.vue'),
