    return target;
  }
}

export function getType(variable: any): string {
  return Object.prototype.toString.call(variable).match(/\[object (.*)\]/)?.[1] || '';
}

export function scrollTop(from: number = 0, to: number, duration: number = 500, endCallback?: () => void) {
  const diff = Math.abs(from - to);
  const step = Math.ceil(diff / duration * 50);

  function scroll(start: number, end: number, step: number) {
    if (start === end) {
      if (endCallback) {
        endCallback();
      }

      return;
    }

    let d = (start + step > end) ? end : start + step;

    if (start > end) {
      d = (start - step < end) ? end : start - step;
    }

    window.scrollTo(d, d);
    window.requestAnimationFrame(() => scroll(d, end, step));
  }

  scroll(from, to, step);
}

export function setMeta(name: string, content: string): void {
  const head = document.getElementsByTagName('head');
  const metaArr = document.getElementsByTagName('meta');

  for (const meta of metaArr) {
    const metaName = meta.getAttribute('name');
    const metaContent = meta.getAttribute('content');
    if (name === metaName && content === metaContent) {
      return;
    }
  }

  const meta = document.createElement('meta');
  meta.name = name;
  meta.content = content;
  head[0]?.appendChild(meta);
}

export function setLocalStorage(key: string, value: any): void {
  if (window?.localStorage) {
    window.localStorage.setItem(key, value);
  }
}

export function getLocalStorage(key: string): string {
  if (window?.localStorage) {
    return window.localStorage.getItem(key) || '';
  }

  return '';
}

export function isExist(val: any): boolean {
  return val !== undefined && val !== null;
}

export function validator(arr: Array<unknown>): (val: unknown) => boolean {
  return (val: unknown) => {
    const result = arr.includes(val);

    if (!result) {
